
public class DemoStringBuffer {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		StringBuilder  sb = new StringBuilder("citi");
		System.out.println(sb);
		
		sb.append(", pune");
		System.out.println(sb);

		sb.insert(4, 'a');
		System.out.println(sb);
		
		
	}

}
